import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import "./UserProfile.scss"
const UserProfile: React.FC = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    password: "",
    password_confirmation: ""
  })

  const [userProfile, setUserProfile] = useState({
    name: "",
    address: "",
    date: "",
    phone: "",
  })

  interface ParamTypes {
    id: string
  }

  function handleInputChange(event: React.FormEvent<HTMLInputElement>) {
    const { name, value } = event.currentTarget;
    setUser({ ...user, [name]: value });
  }

  function handleInputUserProfileChange(event: React.FormEvent<HTMLInputElement>) {
    const { name, value } = event.currentTarget;
    setUserProfile({ ...userProfile, [name]: value });
  }

  const { id } = useParams<ParamTypes>();

  useEffect(() => {
    const requestUrl = "/api/v1/users/" + id;
    async function fetchUser() {
      await axios.get(requestUrl)
        .then(res => {
          const resJSON = JSON.parse(JSON.stringify(res.data));
          setUser(resJSON.user);
          setUserProfile(resJSON.user_profile);
        })
        .catch(error => console.log(error))
    }
    fetchUser();
  }, [])
  return (
    <div style={{ display: "flex" }}>
      <div className="col-md-6">
        <div className="myaccount-content">
          <h3>Account Details</h3>
          <div className="account-details-form">
            <form action="#">
              <div className="row">
                <div className="col-lg-12 col-12 mb-30">
                  <label>Username: </label>
                  <input id="first-name" placeholder="Username" value={user.username} type="text" />
                </div>
                <div className="col-12 mb-30">
                  <label>Email: </label>
                  <input id="email" placeholder="Email Address" value={user.email} type="email" />
                </div>
                <div className="col-12 mb-30">
                  <h4>Password change</h4>
                </div>

                <div className="col-lg-6 col-12 mb-30">
                  <label>New Password: </label>
                  <input id="new-pwd" placeholder="New Password" type="password" />
                </div>
                <div className="col-lg-6 col-12 mb-30">
                  <label>Confirm Password: </label>
                  <input id="confirm-pwd" placeholder="Confirm Password" type="password" />
                </div>
                <div className="col-12">
                  <button className="save-change-btn">Save Changes</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
      <div className="col-md-6">
        <div className="myaccount-content">
          <h3>User Profile Details</h3>
          <div className="account-details-form">
            <form action="#">
              <div className="row">
                <div className="col-lg-12 col-12 mb-30">
                  <label>Full Name: </label>
                  <input id="first-name" value={userProfile.name} placeholder="Full Name" type="text" />
                </div>

                <div className="col-12 mb-30">
                  <label>Date of birth: </label>
                  <input id="display-name" value={userProfile.date} placeholder="Date of birth" type="date" />
                </div>
                <div className="col-12 mb-30">
                  <label>Address: </label>
                  <textarea id="email" value={userProfile.address} placeholder="Address" />
                </div>
                <div className="col-12 mb-30">
                  <label>Phone: </label>
                  <input id="current-pwd" value={userProfile.phone} placeholder="Phone" type="text" />
                </div>
                <div className="col-12">
                  <button className="save-change-btn">Save Changes</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
