import React, { useEffect, useState } from 'react';

interface PaginationProps {
  total: number,
  handleClickPage: (page: number) => void,
  limit: number,
  page: number
};

const Pagination: React.FC<PaginationProps> = props => {
  const total = props.total;
  const page = props.page;
  let totalProducts = 0;
  if (total > props.limit)
    totalProducts = total % props.limit === 0 ? Math.floor(total / props.limit) : Math.floor(total / props.limit) + 1;
  const [paginationList, setPaginationList] = useState([] as number[]);



  useEffect(() => {
    function createPagination() {
      let list = [] as any[];
      if (page <= 3) {
        list.push(1);
        list.push(2);
        list.push(3);
        list.push(totalProducts);
      } else if (page < totalProducts - 2) {
        list.push(page - 1);
        list.push(page);
        list.push(page + 1);
        list.push(totalProducts);
      } else {
        list.push(1);
        list.push(page);
        list.push(page + 1);
        list.push(totalProducts);
      }
      setPaginationList(list);
      console.log(paginationList);
    }
    createPagination();
  }, [])



  return (
    <div>
      <div className="pagination-container">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              {/*=======  pagination-content  =======*/}
              <div className="pagination-content text-center">
                <ul>

                  <li key={1}><button onClick={() => props.handleClickPage(page)}>{paginationList[0]}</button></li>
                  <li key={2}><button onClick={() => props.handleClickPage(page)}>{paginationList[1]}</button></li>
                  <li key={3}><button onClick={() => props.handleClickPage(page)}>{paginationList[2]}</button></li>
                  ...
                  <li key={4}><button onClick={() => props.handleClickPage(page)}>{paginationList[3]}</button></li>
                </ul>
              </div>
              {/*=======  End of pagination-content  =======*/}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pagination;
