import axios from "axios";
import { useEffect, useState } from "react";
import "./SingleProduct.scss";
const SingleProduct: React.FC = (props) => {
  const initProduct = {
    product_name: "",
    price: "",
    description: "",
    image: [],
    category_id: ""
  }




  const [categories, setCategories] = useState([] as any[]);
  const [product, setProduct] = useState(initProduct);

  function handleInputUserProfileChange(event: React.FormEvent<HTMLInputElement>) {
    const { name, value } = event.currentTarget;
    setProduct({ ...product, [name]: value });
    console.log(product);
  }


  useEffect(() => {
    const requestUrl = "http://localhost:3001/api/v1/categories"
    axios.get(requestUrl)
      .then(res => setCategories(res.data))
      .catch(error => console.log(error))
  }, [])
  return (
    <div >
      <form style={{ display: "flex", height: 800 }} className="table-user bg-light">
        <div className="col-md-4 table-user product_detail">
          <div className="col-md-12" style={{ display: "flex" }}>
            <div className="col-md-5 image table-user" ></div>
            <div className="col-md-5 image table-user"></div>
          </div>

          <input type="file" onChange={() => handleInputUserProfileChange} id="file" multiple />
        </div>
        <div className="col-md-7 table-user product_detail">
          <label htmlFor="">Name</label>
          <input type="text" name="product_name" onChange={() => handleInputUserProfileChange} className="form-control" />
          <label htmlFor="">Price</label>
          <input type="text" name="price" onChange={() => handleInputUserProfileChange} className="form-control" />
          <label htmlFor="">Description</label>

          <textarea className="form-control" />
          <label htmlFor="">Categories</label>
          <select className="form-control" name="role" id="role">
            {categories.map(cate => (
              <option value={cate.id}>{cate.category_name}</option>
            ))}
          </select>
          <div className="col-md-12 btn-submit" >
            <input type="submit" value="Submit" className="btn btn-primary" />
          </div>

        </div>

      </form>
    </div>
  );
}

export default SingleProduct;
