import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import AlertSuccess from "../share/AlertSuccess";
import "./UserProfile.scss"
const UserProfile: React.FC = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    password: "",
    password_confirmation: ""
  })

  const [errors, setErrors] = useState([] as any[]);

  const [userProfile, setUserProfile] = useState({
    name: "",
    address: "",
    date: "",
    phone: "",
  })

  const { id } = useParams<ParamTypes>();
  const requestUrl = "/api/v1/users/" + id;
  const qs = require("qs");

  interface ParamTypes {
    id: string
  }

  function handleInputChange(event: React.FormEvent<HTMLInputElement>) {
    const { name, value } = event.currentTarget;
    setUser({ ...user, [name]: value });
  }

  function handleInputUserProfileChange(event: React.FormEvent<HTMLInputElement>) {
    const { name, value } = event.currentTarget;
    setUserProfile({ ...userProfile, [name]: value });
  }

  async function handleAccountSubmit(e: React.FormEvent) {

    await axios.patch(requestUrl, qs.stringify({
      user: {
        username: user.username,
        email: user.email,
        password: user.password,
        password_confirmation: user.password_confirmation
      }
    }))
      .then(res => {
        const resJSON = JSON.parse(JSON.stringify(res.data));
        if (resJSON.success)
          window.location.href = "/admin";
        else {
          let errors: any[] = resJSON.errors;
          let list: any[] = [];
          Object.entries(errors).forEach(
            ([key, value]) => {
              list.push(key + " " + value);
            }
          );
          setErrors(list);
        }
      })

  }

  useEffect(() => {
    async function fetchUser() {
      await axios.get(requestUrl)
        .then(res => {
          const resJSON = JSON.parse(JSON.stringify(res.data));
          setUser(resJSON.user);
          setUserProfile(resJSON.user_profile);
        })
        .catch(error => console.log(error))
    }
    fetchUser();
  }, [])
  return (
    <div style={{ display: "flex" }}>
      <div className="col-md-6">
        <div className="myaccount-content">
          <h3>Account Details</h3>
          <div className="account-details-form">
            <form action="#" onSubmit={handleAccountSubmit}>
              <div className="row">
                <AlertSuccess errors={errors} />
              </div>
              <div className="row">
                <div className="col-lg-12 col-12 mb-30">
                  <label>Username: </label>
                  <input id="first-name" name="username" placeholder="Username" onChange={handleInputChange} value={user.username} type="text" />
                </div>
                <div className="col-12 mb-30">
                  <label>Email: </label>
                  <input id="email" name="email" placeholder="Email Address" onChange={handleInputChange} value={user.email} type="email" />
                </div>
                <div className="col-12 mb-30">
                  <h4>Password change</h4>
                </div>

                <div className="col-lg-6 col-12 mb-30">
                  <label>New Password: </label>
                  <input id="new-pwd" name="password" placeholder="New Password" onChange={handleInputChange} type="password" />
                </div>
                <div className="col-lg-6 col-12 mb-30">
                  <label>Confirm Password: </label>
                  <input id="confirm-pwd" name="password_confirmation" placeholder="Confirm Password" onChange={handleInputChange} type="password" />
                </div>
                <div className="col-12">
                  <button className="save-change-btn">Save Changes</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
      <div className="col-md-6">
        <div className="myaccount-content">
          <h3>User Profile Details</h3>
          <div className="account-details-form">
            <form action="#">
              <div className="row">
                <div className="col-lg-12 col-12 mb-30">
                  <label>Full Name: </label>
                  <input id="first-name" value={userProfile.name} name="name" onChange={handleInputUserProfileChange} placeholder="Full Name" type="text" />
                </div>

                <div className="col-12 mb-30">
                  <label>Date of birth: </label>
                  <input id="display-name" value={userProfile.date} name="date" onChange={handleInputUserProfileChange} placeholder="Date of birth" type="date" />
                </div>
                <div className="col-12 mb-30">
                  <label>Address: </label>
                  <textarea id="email" value={userProfile.address} name="address" placeholder="Address" />
                </div>
                <div className="col-12 mb-30">
                  <label>Phone: </label>
                  <input id="current-pwd" value={userProfile.phone} name="phone" onChange={handleInputUserProfileChange} placeholder="Phone" type="text" />
                </div>
                <div className="col-12">
                  <button className="save-change-btn">Save Changes</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
