import axios from "axios";
import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { useParams } from "react-router";
import AlertSuccess from "../share/AlertSuccess";
import "./UserProfile.scss"
const UserProfile: React.FC = () => {
  const [user, setUser] = useState({
    id: "1",
    username: "",
    email: "",
    role: 0,
    password: "",
    password_confirmation: ""
  })

  const [errors, setErrors] = useState([] as any[]);
  const [errorsUserProfile, setErrorsUserProfile] = useState([] as any[]);

  const [userProfile, setUserProfile] = useState({
    id: "1",
    name: "",
    address: "",
    date_of_birth: "",
    phone: "",
    user_id: 1
  })

  const { t } = useTranslation();
  const { id } = useParams<ParamTypes>();
  const requestUrl = "http://localhost:3001/api/v1/users/" + id;
  const qs = require("qs");

  interface ParamTypes {
    id: string
  }

  function handleAccountSubmit(e: React.FormEvent) {
    e.preventDefault();
    console.log(user);
    let role = document.getElementById("role") as HTMLSelectElement;
    let roleI: number = +role.value;
    setUser({
      ...user,
      role: roleI,
    })
    console.log(role.value);
    axios.patch(requestUrl, qs.stringify({
      user: {
        username: user.username,
        email: user.email,
        password: user.password,
        role: user.role,
        password_confirmation: user.password_confirmation
      }
    }))
      .then(res => {
        const resJSON = JSON.parse(JSON.stringify(res.data));
        if (resJSON.success)
          console.log(resJSON.user)
        else {
          let errors: any[] = resJSON.errors;
          let list: any[] = [];
          Object.entries(errors).forEach(
            ([key, value]) => {
              list.push(key + " " + value);
            }
          );
          setErrors(list);
        }
      })
  }

  function handleInputChange(event: React.FormEvent<HTMLInputElement>) {
    const { name, value } = event.currentTarget;
    setUser({ ...user, [name]: value });
  }

  function handleInputUserProfileChange(event: React.FormEvent<HTMLInputElement>) {
    const { name, value } = event.currentTarget;
    setUserProfile({ ...userProfile, [name]: value });
  }

  async function handleUserProfileSubmit(e: React.FormEvent) {
    e.preventDefault();
    let address = document.getElementById("address") as HTMLTextAreaElement;
    setUserProfile({
      ...userProfile,
      address: address.value
    })
    const url = "http://localhost:3001/api/v1/user_profiles/" + userProfile.id
    await axios.patch(url, qs.stringify({
      user_profile: {
        id: userProfile.id,
        name: userProfile.name,
        address: userProfile.address,
        date_of_birth: userProfile.date_of_birth,
        phone: userProfile.phone,
        user_id: user.id
      }
    }))
      .then(res => {
        const resJSON = JSON.parse(JSON.stringify(res.data));
        if (resJSON.success)
          window.location.href = "/admin";
        else {
          let errors: any[] = resJSON.errors;
          let list: any[] = [];
          Object.entries(errors).forEach(
            ([key, value]) => {
              list.push(key + " " + value);
            }
          );
          setErrorsUserProfile(list);
        }
      })
  }


  useEffect(() => {
    async function fetchUser() {
      await axios.get(requestUrl)
        .then(res => {
          const resJSON = JSON.parse(JSON.stringify(res.data));
          setUser(resJSON.user);
          setUserProfile(resJSON.user_profile);
        })
        .catch(error => console.log(error))
    }
    fetchUser();
  }, [])
  return (
    <div style={{ display: "flex" }}>
      <div className="col-md-6">
        <div className="myaccount-content">
          <h3>Account Details</h3>
          <div className="account-details-form">
            <form onSubmit={handleAccountSubmit}>
              <div className="row">
                <AlertSuccess errors={errors} />
              </div>
              <div className="row">
                <div className="col-lg-12 col-12 mb-30">
                  <label>Username: </label>
                  <input name="username" placeholder="Username" onChange={handleInputChange} defaultValue={user.username} type="text" />
                </div>
                <div className="col-12 mb-30">
                  <label>Email: </label>
                  <input name="email" placeholder="Email Address" onChange={handleInputChange} defaultValue={user.email} type="email" />
                </div>
                <div className="col-12 mb-30 form-group">
                  <label>Role: </label>

                  {
                    user.role === "admin" ? (
                      <select className="form-control" name="role" id="role">
                        <option value="0">{t("useradmin.admin")}</option>
                        <option value="1">{t("useradmin.customer")}</option>
                      </select>
                    ) : (
                      <select className="form-control" name="role" id="role">
                        <option value="1">{t("useradmin.customer")}</option>
                        <option value="0">{t("useradmin.admin")}</option>
                      </select>
                    )
                  }
                </div>
                <div className="col-12 mb-30">
                  <h4>Password change</h4>
                </div>

                <div className="col-lg-6 col-12 mb-30">
                  <label>New Password: </label>
                  <input name="password" placeholder="New Password" onChange={handleInputChange} type="password" />
                </div>
                <div className="col-lg-6 col-12 mb-30">
                  <label>Confirm Password: </label>
                  <input name="password_confirmation" placeholder="Confirm Password" onChange={handleInputChange} type="password" />
                </div>
                <div className="col-12">
                  <button className="save-change-btn">Save Changes</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
      <div className="col-md-6">
        <div className="myaccount-content">
          <h3>User Profile Details</h3>
          <div className="account-details-form">
            <form onSubmit={handleUserProfileSubmit}>
              <div className="row">
                <AlertSuccess errors={errorsUserProfile} />
              </div>
              <div className="row">
                <div className="col-lg-12 col-12 mb-30">
                  <label>Full Name: </label>
                  <input defaultValue={userProfile.name} name="name" onChange={handleInputUserProfileChange} placeholder="Full Name" type="text" />
                </div>

                <div className="col-12 mb-30">
                  <label>Date of birth: </label>
                  <input defaultValue={userProfile.date_of_birth} name="date_of_birth" onChange={handleInputUserProfileChange} placeholder="Date of birth" type="date" />
                </div>
                <div className="col-12 mb-30">
                  <label>Address: </label>
                  <textarea id="address" defaultValue={userProfile.address} name="address" placeholder="Address" />
                </div>
                <div className="col-12 mb-30">
                  <label>Phone: </label>
                  <input id="phone" defaultValue={userProfile.phone} name="phone" onChange={handleInputUserProfileChange} placeholder="Phone" type="text" />
                </div>
                <div className="col-12">
                  <button className="save-change-btn">Save Changes</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserProfile;
